package EBox::dlllciasrouter::Model::ManualDomainName;

use base 'EBox::Model::DataForm';

use strict;
use warnings;

use EBox::Global;
use EBox::Gettext;

use EBox::Types::Text;
use EBox::Types::MailAddress;


use Try::Tiny;

sub getOptions
{
    my $options = ();
    $options->{moduleName} = 'ManualDomainName';
    return $options;
}

# ------------------------------------------

sub new
{
    my ($class, %params) = @_;

    my $self = $class->SUPER::new(%params);
    bless($self, $class);

    return $self;
}

sub _table
{
    my ($self) = @_;

    my $options = $self->getOptions();

    my $lib = $self->parentModule()->model('LibraryToolkit');
    my $fieldsFactory = $self->getLoadLibrary('LibraryFields');

    my @fields = ();

    my $tableName = $options->{moduleName};

    my $desc = "";

    push(@fields, new EBox::Types::Text(
        'fieldName'  => 'content',
        'printableName' => __('Content'),
        'editable' => 0,
        'optional' => 0,
        'hiddenOnSetter' => 0,
        'hiddenOnViewer' => 1,
        'help' => $self->getLoadLibrary('LibraryFields')->createFieldDescriptionEditor(),
        'defaultValue' => $desc,
        'allowUnsafeChars' => 1,
    ));

    my $pageTitle = __('Domain Name Manual');
 
    my $configView = '/dlllciasrouter/Composite/SettingComposite';
    my $dataTable =
        {
            'tableName' => $tableName,
            'pageTitle' => $pageTitle,
            'printableTableName' => $pageTitle,
            'modelDomain'     => 'dlllciasrouter',
            'defaultActions' => [ 'editField' ],
            'tableDescription' => \@fields,
            'HTTPUrlView'=> 'dlllciasrouter/View/' . $tableName,
            'messages' => {
                'update' => 'DONE <script type="text/javascript">location.href="'.$configView.'";</script>',
            }
        };

    return $dataTable;
}

# -------------------------------------------------------------

##
# 讀取指定的Model
sub getLoadLibrary
{
    my ($self, $library) = @_;
    return $self->parentModule()->model($library);
}

1;
